import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Medicament() {
  const [meds, setMeds] = useState([]);
  const navigate = useNavigate();
  function getMedsDetails() {
    axios.get("http://localhost:5000/api/medicaments").then((response) => {
      setMeds(response.data);
    });
  }
  useEffect(() => {
    getMedsDetails();
  }, []);

  function deleteMed(numeMed) {
    axios
      .delete("http://localhost:5000/api/medicaments", {
        data: { Nume: numeMed },
      })
      .then((response) => {
        setMeds((element) => element.filter((meds) => meds.Nume !== numeMed));
      });
  }
  return (
    <div>
      <div>
        <input type="text" id="countryFilter" placeholder="Medicament " />
        <button type="button" class="btn btn-success">
          Search
        </button>
      </div>
      <div class="table-responsive">
        <table class="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Weight</th>
              <th>Stock</th>
              <th>Expiration Date</th>
            </tr>
          </thead>
          <tbody>
            {meds.map((meds) => {
              console.log(meds);
              return (
                <tr>
                  <td>{meds.Nume}</td>
                  <td>{meds.Gramaj}</td>
                  <td>{meds.Stoc}</td>
                  <td>{meds.Data_expirare}</td>
                  <td>
                    <button
                      type="button"
                      class="btn"
                      onClick={() => deleteMed(meds.Nume)}
                    >
                      <i class="fa fa-trash"></i>
                    </button>
                  </td>
                  <td>
                    <button
                      type="button"
                      class="btn "
                      data-toggle="modal"
                      data-target="#myModal"
                    >
                      <i class="fa fa-edit"></i>
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <button type="button" class="close" data-dismiss="modal">
                &times;
              </button>
              <h4 class="modal-title">Modal Header</h4>
            </div>
            <div class="modal-body">
              <p>Some text in the modal.</p>
            </div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-default"
                data-dismiss="modal"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Medicament;
