import axios from "axios";
import { useState, useEffect } from "react";

function Command() {
  const [addresses, setAddresses] = useState([]);
  const [inputList, setInputList] = useState([]);
  const [med, setMed] = useState([]);
  console.log(inputList);
  function getAddresses() {
    const email = localStorage.getItem("userEmail");
    axios
      .get("http://localhost:5000/api/addresses/" + email)
      .then((response) => {
        console.log(response);
        setAddresses(response.data);
      });
  }
  function getMeds() {
    axios.get("http://localhost:5000/api/medicaments").then((response) => {
      console.log(response);
      setMed(response.data);
    });
  }
  useEffect(() => {
    getAddresses();
    getMeds();
  }, []);

  const onAddBtnClick = () => {
    setInputList(
      inputList.concat(
        <div className="row">
          <div className="col-sm-6">
            <label htmlFor="email" class="form-label">
              Medicament:
            </label>
            <select class="form-select">
              {med?.map((medElem) => {
                return <option>{medElem.Nume}</option>;
              })}
            </select>
          </div>
          <div className="col-sm-6">
            <label htmlFor="quantity" class="form-label">
              Quantity:
            </label>
            <input
              type="text"
              class="form-control"
              id="quantity"
              placeholder="Enter quantity"
              name="quantity"
            />
          </div>
        </div>
      )
    );
  };

  return (
    <div>
      <div className="mainAddMedicament">
        <div className="container d-flex align-items-center justify-content-center  pt-5">
          <form>
            <div className="row">
              <h1 className="text-center mt-2 mb-4">Order Information</h1>
              <div className="col-sm-6 mb-3 mt-3">
                <div className="">
                  <label htmlFor="Name" className="form-label">
                    Name:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nameMedicament"
                    placeholder="Enter name"
                    name="Name"
                    // value={med.nameMed}
                    // onChange={changeMed}
                  />
                </div>
              </div>
              <div className="col-sm-6 ">
                <div className="mb-3 mt-3">
                  <label htmlFor="address" className="form-label">
                    Address:
                  </label>
                  <select className="form-select">
                    {addresses?.map((address, index) => {
                      return (
                        <option key={index}>
                          {address.Tip_adresa}, {address.Oras},{address.Judet},
                          {address.Nr},{address.Cod_postal}
                        </option>
                      );
                    })}
                  </select>
                </div>
              </div>

              <div>
                <button
                  type="button"
                  className="mb-3 mt-3 btn btn-success"
                  onClick={onAddBtnClick}
                >
                  Add Medicament
                </button>
              </div>
              {inputList}
              <div>
                <button type="button" className="mb-3 mt-2 btn btn-success">
                  Order Recipe
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Command;
