import axios from "axios";
import { useState, useEffect } from "react";

const Input = () => {
  return (
    <div className="row">
      <div className="col-sm-6">
        <select class="form-select">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
        </select>
      </div>
      <div className="col-sm-6">
        <label for="email" class="form-label">
          Email:
        </label>
        <input
          type="email"
          class="form-control"
          id="email"
          placeholder="Enter email"
          name="email"
        />
      </div>
    </div>
  );
};

function Command() {
  const [addresses, setAddresses] = useState([]);
  const [inputList, setInputList] = useState([]);

  function getAddresses() {
    const email = localStorage.getItem("userEmail");
    axios
      .get("http://localhost:5000/api/addresses/" + email)
      .then((response) => {
        console.log(response);
        setAddresses(response.data);
      });
  }
  useEffect(() => {
    getAddresses();
  }, []);

  const onAddBtnClick = event => {
    setInputList(inputList.concat(<Input key={inputList.length} />));
  };

  function createSpace() {}
  return (
    <div>
      <div className="mainAddMedicament">
        <div className="container d-flex align-items-center justify-content-center  pt-5">
          <form>
            <div className="row">
              <h1 className="text-center mt-2 mb-4">Order Information</h1>
              <div className="col-sm-6 mb-3 mt-3">
                <div className="">
                  <label htmlFor="Name" className="form-label">
                    Name:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="nameMedicament"
                    placeholder="Enter name"
                    name="Name"
                    // value={med.nameMed}
                    // onChange={changeMed}
                  />
                </div>
              </div>
              <div className="col-sm-6 ">
                <div className="mb-3 mt-3">
                  <label htmlFor="address" className="form-label">
                    Address:
                  </label>
                  <select className="form-select">
                    {addresses?.map((address, index) => {
                      return (
                        <option key={index}>
                          {address.Tip_adresa}, {address.Oras},{address.Judet},
                          {address.Nr},{address.Cod_postal}
                        </option>
                      );
                    })}
                  </select>
                </div>
              </div>

              <div>
                <button
                  type="button"
                  className="mb-3 mt-3 btn btn-success"
                  onClick={onAddBtnClick}
                >
                  Add Medicament
                </button>
              </div>
              {inputList}
              <div>
                <button type="button" className="mb-3 mt-2 btn btn-success">
                  Order Recipe
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Command;
